import com.app.micimpact.business.service.SearchTagsService;
import com.app.micimpact.business.service.TagsService;
import com.app.micimpact.rest.common.AbstractRestController;

/**
 * Spring MVC controller for 'Tags' management.
 */
@RequestMapping("/tag")
@Controller
public class TagsRestController  extends AbstractRestController {

	@Resource
	private TagsService tagsService;
	
	@Resource
	private SearchTagsService searchTagsService;
	
	
	@RequestMapping( value="/popular_tags",
			method = {RequestMethod.GET, RequestMethod.POST},
			produces = MediaType.APPLICATION_JSON_VALUE)
    @ResponseStatus(HttpStatus.OK)
	@ResponseBody
	public HashMap<String, Object> popularTags(
			@RequestParam(value="last_id", required=false) Integer lastId,
			@RequestParam(value="count", defaultValue="5") Integer count) 
	{
		HashMap<String, Object> ret = new HashMap<String, Object> ();
		
		List<String> tags = new ArrayList<String>();
		for (SearchTags it : searchTagsService.findAll())
			tags.add(it.getName());
		
		ret.put("tags", tags);
		return SUCCESS(ret);
	}

	
}
